mapper.select:
         select   c.company_id,  c.company_name,  c.user_limit, c.expiration_date, i.contact_person , i.phone_number ,
p.province_name as province , b.business_name as  business ,a.agency_name as  agency,
 c.created_at as start ,c.indicator
 from company c join company_info i on c.company_id =i.company_id
join province p on i.province_id =p.province_id
join business b on c.business_id =b.business_id
join  agency  a on c.agency_id =a.agency_id
         where  1=1
         and c.company_name like '%${company_name}%'
         and i.province_id =#{province_id} 
         and c.business_id=#{business_id} 
         and c.agency_id =#{agency_id} 

         and c.created_at  > #{start}
         and c.created_at < #{end}
   LIMIT #{skip},#{take};

select  count(*)
from company c join company_info i on c.company_id =i.company_id
join province p on i.province_id =p.province_id
join business b on c.business_id =b.business_id
join  agency  a on c.agency_id =a.agency_id
where  1=1
and c.company_name like '%${company_name}%'
and i.province_id =#{province_id} 
and c.business_id=#{business_id} 
and c.agency_id =#{agency_id} 

and c.created_at  > #{start}
and c.created_at < #{end}
