mapper.select:
select result.*, c1.study_count,c2.collect_count , c3.courseCollected, c4.star from ( select c.* , u.full_name as principal_user_idName from course c left join user u on c.principal_user_id=u.user_id where u.indicator=1 and c.indicator=1 and course_id=#{id}) result join (
select count( distinct user_id) as study_count from learn_progress where course_id=#{id})c1 on 1=1 join ( select count( user_id) as collect_count from user_favor where item_type='course' and item_id=#{id} )c2 on 1=1 join ( select case when count(*) >0 then 1 else 0 end as courseCollected from user_favor where item_type='course' and item_id=#{id} and user_id=#{userid})c3 on 1=1
join ( select case when total is not null then total/ct else 0 end as star from ( select sum(star) as total , count(comment_id) as ct from course_comment where course_id =#{id})t)c4 on 1=1;

select result.* , case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o3.userct is not null then o3.userct else 0 end as collect_count, case when o2.pingfen is not null then o2.pingfen else 0 end as star from ( select c.* from course c left join course_whitelist w on c.course_id=w.course_id where indicator=1 and company_id=#{companyid} and ( type_id=1 or ( type_id=2 and w.user_id=#{userid} ) ) )result
left join ( select course_id, count(*) as renshu from ( select course_id, user_id from learn_progress group by course_id, user_id)t group by course_id)o1 on result.course_id =o1.course_id left join ( select course_id, total/ct as pingfen from ( select course_id, sum(star) as total , count(comment_id) as ct from course_comment group by course_id)t)o2 on result.course_id= o2.course_id left join ( select item_id as course_id, count(user_id) as userct from user_favor where item_type='course' group by item_id)o3 on result.course_id= o3.course_id
JOIN ( select a.course_id from (select course_id , count(*) as count from learn_progress where user_id=#{userid} group by course_id )a join ( select course_id , count(*) as count from course_content group by course_id )b on a.course_id =b. course_id and a.count < b.count
)o4 on result.course_id=o4.course_id order by renshu desc, pingfen desc limit #{skip},#{take};

select result.* , case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o3.userct is not null then o3.userct else 0 end as collect_count, case when o2.pingfen is not null then o2.pingfen else 0 end as star from ( select c.* from course c left join course_whitelist w on c.course_id=w.course_id where indicator=1 and company_id=#{companyid} and ( type_id=1 or ( type_id=2 and w.user_id=#{userid} ) ) )result
left join ( select course_id, count(*) as renshu from ( select course_id, user_id from learn_progress group by course_id, user_id)t group by course_id)o1 on result.course_id =o1.course_id left join ( select course_id, total/ct as pingfen from ( select course_id, sum(star) as total , count(comment_id) as ct from course_comment group by course_id)t)o2 on result.course_id= o2.course_id left join ( select item_id as course_id, count(user_id) as userct from user_favor where item_type='course' group by item_id)o3 on result.course_id= o3.course_id
JOIN ( select a.course_id from (select course_id , count(*) as count from learn_progress where user_id=#{userid} group by course_id )a join ( select course_id , count(*) as count from course_content group by course_id )b on a.course_id =b. course_id and a.count =b.count)o4 on result.course_id=o4.course_id order by renshu desc, pingfen desc limit #{skip},#{take};

select result.* , case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o3.userct is not null then o3.userct else 0 end as collect_count,
case when o2.pingfen is not null then o2.pingfen else 0 end as star from ( select * from course where indicator=1 and course_id IN ( select item_id from user_favor where user_id=#{userid} and item_type='course' ) )result
left join ( select course_id, count(*) as renshu from ( select course_id, user_id from learn_progress group by course_id, user_id)t group by course_id)o1 on result.course_id =o1.course_id left join ( select course_id, total/ct as pingfen from ( select course_id, sum(star) as total , count(comment_id) as ct from course_comment group by course_id)t)o2 on result.course_id= o2.course_id left join ( select item_id as course_id, count(user_id) as userct from user_favor where item_type='course' group by item_id)o3 on result.course_id= o3.course_id order by renshu desc, pingfen desc limit #{skip},#{take};

mapper.update:
update course_content
sequnce_num=#{sequnce_num}, sequnce_title=#{sequnce_title}, content=#{content}, filename=#{filename}, content_type= #{content_type}
where content_id=#{content_id}

update course
ispublished =#{status} , publish_date =now()
where course_id = #{courseid}
