select * , count - study_count as left_count from ( select result.* , case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o2.userct is not null then o2.userct else 0 end as collect_count, case when o3.pingfen is not null then o3.pingfen else 0 end as star from( select p.* , e.title, e.intro, e.icon, e.pic,e.company_id from enrollment_period p join enrollment e on p.enrollment_id= e.enrollment_id where now() >=start_date and now() < end_date and p.indicator=1 and e.indicator=1 and e.company_id=#{companyid})result left join ( select period_id, count(*) as renshu from user_enrollment group by period_id )o1 on result.period_id =o1.period_id left join( select item_id as period_id, count(user_id) as userct from user_favor where item_type='enrollment' group by item_id)o2 on result.period_id= o2.period_id
left join( select enrollment_id, total/ct as pingfen from ( select enrollment_id, sum(star) as total , count(comment_id) as ct from enrollment_comment group by enrollment_id)t)o3 on result.enrollment_id= o3.enrollment_id
)t where count > study_count order by study_count desc, start_date desc limit #{skip},#{take};

select * , count - study_count as left_count from ( select result.* , case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o2.userct is not null then o2.userct else 0 end as collect_count, case when now() < end_date then 0 else 1 end as isFinished, case when o3.period_id is null then 0 else 1 end as isReminderAdded,
case when o4.pingfen is not null then o4.pingfen else 0 end as star from( select p.* , e.title, e.intro, e.icon, e.pic,e.company_id from enrollment_period p join enrollment e on p.enrollment_id= e.enrollment_id where p.indicator=1 and e.indicator=1 and e.company_id=#{companyid})result left join ( select period_id, count(*) as renshu from user_enrollment group by period_id )o1 on result.period_id =o1.period_id left join( select item_id as period_id, count(user_id) as userct from user_favor where item_type='enrollment' group by item_id)o2 on result.period_id= o2.period_id
left join( select * from user_enrollment_reminder where user_id= #{userid})o3 on result.period_id= o3.period_id
left join( select enrollment_id, total/ct as pingfen from ( select enrollment_id, sum(star) as total , count(comment_id) as ct from enrollment_comment group by enrollment_id)t)o4 on result.enrollment_id= o4.enrollment_id
)t where count <= study_count or now() >= end_date order by study_count desc, start_date desc limit #{skip},#{take};

select * , count - study_count as left_count from ( select result.* , case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o2.userct is not null then o2.userct else 0 end as collect_count, case when now() < end_date then 0 else 1 end as isFinished, case when o4.pingfen is not null then o4.pingfen else 0 end as star from( select p.* , e.title, e.intro, e.icon, e.pic,e.company_id from enrollment_period p join enrollment e on p.enrollment_id= e.enrollment_id where p.indicator=1 and e.indicator=1 and e.company_id=#{companyid})result left join ( select period_id, count(*) as renshu from user_enrollment group by period_id )o1 on result.period_id =o1.period_id left join( select item_id as period_id, count(user_id) as userct from user_favor where item_type='enrollment' group by item_id)o2 on result.period_id= o2.period_id
join (
select period_id from user_enrollment where user_id=#{userid} )o3 on result.period_id= o3.period_id
left join( select enrollment_id, total/ct as pingfen from ( select enrollment_id, sum(star) as total , count(comment_id) as ct from enrollment_comment group by enrollment_id)t)o4 on result.enrollment_id= o4.enrollment_id
)t order by study_count desc, start_date desc limit #{skip},#{take};

      select * from (
       select  p.period_id,   e.enrollment_id,  e.title, p.teacher,DATE_FORMAT(p.start_date,'%Y-%m-%d')  as   start,  DATE_FORMAT(p.end_date,'%Y-%m-%d')  as end,   p.count  ,p.created_at from  enrollment_period  p  join enrollment  e   on p.enrollment_id = e.enrollment_id
where  p.indicator=1 and e.indicator =1 and   e.company_id=#{company_id})t
where  1=1
       and title like '%${title}%'
       and teacher like '%${teacher}%' 
       and created_at >  #{startCondition}
       and created_at <   #{endCondition}
       order by    start desc    limit #{skip}, #{take}
