mapper.select:
select * , count - study_count as left_count from ( select result.* , case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o2.userct is not null then o2.userct else 0 end as collect_count, case when now() < end_date then 0 else 1 end as isFinished from( select p.* , e.title, e.intro, e.icon, e.pic,e.company_id from enrollment_period p join enrollment e on p.enrollment_id= e.enrollment_id where p.indicator=1 and e.indicator=1 and e.company_id=#{companyid})result left join ( select period_id, count(*) as renshu from user_enrollment group by period_id )o1 on result.period_id =o1.period_id left join( select item_id as period_id, count(user_id) as userct from user_favor where item_type='enrollment' group by item_id)o2 on result.period_id= o2.period_id
join (
select period_id from user_enrollment where user_id=#{userid} )o3 on result.period_id= o3.period_id
)t order by study_count desc, start_date desc limit #{skip},#{take};
