mapper.select:
select * from ( select result.*, case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o2.userct is not null then o2.userct else 0 end as collect_count from ( select * from exam where indicator =1 and company_id=#{companyid} and now() < end_date )result
left join ( select exam_id, count(*) as renshu from ( select exam_id, user_id from exam_history group by exam_id, user_id)t group by exam_id)o1 on result.exam_id=o1.exam_id
left join ( select item_id as exam_id, count(user_id) as userct from user_favor where item_type='exam' group by item_id)o2 on result.exam_id=o2.exam_id)t order by start_date limit #{skip} ,#{take}

        select q.* ,r.status as lastStatus from (  select qu.*  ,m.question_num  from question qu join exam_question  m
 on qu.question_id  =m.question_id where    m.exam_id=#{examid} and  qu.indicator =1  )  q
left join ( select * from exam_record  where history_id in
(select max(history_id) from  exam_history where exam_id=#{examid} and user_id=#{userid}  and status ='in-process' group  by history_id)
 )r on q.question_id= r.question_id  order by q.question_num;

             select a.* , case when  r.answer_id  is not null then 1 else 0 end as isSelected
 from (select * from answer where question_id=#{questionid} and indicator =1)a left join
 ( select answer_id from exam_record_answerchoice where record_id in(
 select record_id from exam_record  where  question_id=#{questionid} and history_id =
(select max(history_id) from  exam_history where exam_id= #{examid} and user_id=#{userid}
 and status ='in-process')))r on  a.answer_id =r.answer_id;

          select * from (
select result.*,
        case when o1.renshu  is not null then o1.renshu else 0 end  as study_count,
        case when  o2.userct is not null then  o2.userct else 0  end   as collect_count
        from (
        select * from exam  where indicator =1   and company_id=#{companyid}
        )result

        left join (
        select exam_id, count(*)  as  renshu from (
        select  exam_id, user_id from exam_history  group by exam_id, user_id)t group by exam_id)o1
        on result.exam_id=o1.exam_id

        left join
        (
        select item_id as exam_id,  count(user_id) as userct from  user_favor where item_type='exam' group by item_id)o2
          on result.exam_id=o2.exam_id
         join
        (
        select exam_id from  exam_history  where    user_id=#{userid}     and `status`='pass'  group by  exam_id
        ) o3 on    result.exam_id=o3.exam_id

     )t   order by   start_date limit #{skip} ,#{take}

select * from ( select result.*, case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o2.userct is not null then o2.userct else 0 end as collect_count from ( select * from exam where indicator =1 and company_id=#{companyid} )result
left join ( select exam_id, count(*) as renshu from ( select exam_id, user_id from exam_history group by exam_id, user_id)t group by exam_id)o1 on result.exam_id=o1.exam_id
left join ( select item_id as exam_id, count(user_id) as userct from user_favor where item_type='exam' group by item_id)o2 on result.exam_id=o2.exam_id
join ( select distinct exam_id from exam_history where user_id=#{userid} and exam_id not in ( select exam_id from exam_history where user_id=#{userid} and `status`='pass' group by exam_id) ) o3 on result.exam_id=o3.exam_id
)t order by start_date limit #{skip} ,#{take}

select * from ( select result.*, case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o2.userct is not null then o2.userct else 0 end as collect_count from ( select * from exam where indicator =1 and exam_id in (select item_id from user_favor where user_id=#{userid} and item_type='exam') )result
left join ( select exam_id, count(*) as renshu from ( select exam_id, user_id from exam_history group by exam_id, user_id)t group by exam_id)o1 on result.exam_id=o1.exam_id
left join ( select item_id as exam_id, count(user_id) as userct from user_favor where item_type='exam' group by item_id)o2 on result.exam_id=o2.exam_id)t order by start_date limit #{skip} ,#{take}
