mapper.update:
update learn_task set task_title= #{task_title}, task_description=#{task_description}, task_scope=#{task_scope}, expiration_date=#{expiration_date},
updated_at=NOW()
where task_id=#{task_id};

update learn_task
ispublished =#{status} , publish_date =now()
where task_id = #{taskid}

mapper.select:
       select u.full_name,  u.job_number ,  u.department,  u.area  , t.task_title,
 t.expiration_date,  u.user_id , t.task_id  from  learn_task  t join task_user_mapping m  on t.task_id=   m.task_id  join  user u
ON  m.user_id = u.user_id   where  t.task_id=#{task_id} and     u.indicator=1   limit #{skip}, #{take}

select  task_title ,task_scope,  expiration_date, ispublished,  task_id from  learn_task
where 1=1  and indicator=1
and task_title like '%${task_title}%'
and expiration_date >=    #{start_date} 
and expiration_date  <=   #{end_date}
limit #{skip} ,#{take}

select  count(*) from  learn_task
where 1=1  and indicator=1
and task_title like '%${task_title}%'
and expiration_date >=    #{start_date} 
and expiration_date  <=   #{end_date}

select * from ( select result.* , case when o1.renshu is not null then o1.renshu else 0 end as study_count, case when o2.userct is not null then o2.userct else 0 end as collect_count, case when o3.pingfen is not null then o3.pingfen else 0 end as star from(
select p.*, e.title, e.intro, e.icon, e.pic,e.company_id from enrollment e join enrollment_period p on e.enrollment_id = p.enrollment_id join task_item_mapping m on p.period_id = m.item_id where m.item_type='enrollment' and m.task_id=#{taskid} and e.indicator=1 and p.indicator=1 and p.ispublished =1
)result left join ( select period_id, count(*) as renshu from user_enrollment group by period_id )o1 on result.period_id =o1.period_id left join( select item_id as period_id, count(user_id) as userct from user_favor where item_type='enrollment' group by item_id)o2 on result.period_id= o2.period_id
left join( select enrollment_id, total/ct as pingfen from ( select enrollment_id, sum(star) as total , count(comment_id) as ct from enrollment_comment group by enrollment_id)t)o3 on result.enrollment_id= o3.enrollment_id
)t order by study_count desc, start_date desc;
